INSERT INTO Recipes (Name, Description, Ingredients, Category, SubmissionDate, ImageUrl)
VALUES (
    'Spicy Mutton Biryani',
    'Spicy Mutton Biryani is a flavorful and aromatic one-pot dish that is perfect for special occasions. Follow these steps to prepare this delicious biryani:\n\n
    1. Wash and soak 2 cups of long-grain Basmati rice in water for 30 minutes. Drain and set aside.
    2. In a large, heavy-bottomed pot, heat 3 tablespoons of ghee or oil over medium heat. Add whole spices like bay leaves, cloves, and cardamom pods.
    3. Add finely sliced onions and sauté until they turn golden brown.
    4. Add ginger-garlic paste and green chilies. Sauté for a couple of minutes until the raw smell disappears.
    5. Add mutton pieces and cook until they turn brown on all sides.
    6. Sprinkle in spices like turmeric powder, red chili powder, and biryani masala. Mix well.
    7. Add yogurt and cook until the oil separates.
    8. Layer the soaked rice over the mutton mixture. Sprinkle saffron milk and fried onions on top.
    9. Cover the pot and cook on low heat for about 30-40 minutes until the rice is tender and the mutton is cooked through.
    10. Garnish with fresh coriander and mint leaves. Serve hot!',
    '- 500 grams Basmati Rice\n- 400 grams Mutton\n- 3 tablespoons Ghee or Oil\n- 2 Onions, thinly sliced\n- 2 Bay Leaves\n- 4-5 Cloves\n- 4-5 Green Cardamom Pods\n- 1 tablespoon Ginger-Garlic Paste\n- 2-3 Green Chilies, slit\n- 1/2 teaspoon Turmeric Powder\n- 2 teaspoons Red Chili Powder\n- 2 tablespoons Biryani Masala\n- 1/2 cup Yogurt\n- A pinch of Saffron soaked in 2 tablespoons of Milk\n- Fresh Coriander and Mint Leaves for garnish\n- Salt to taste',
    'Mutton',
    '2023-10-09',
    'https://static.wixstatic.com/media/91e241_e4c09e48b2ab442bae84592abe07d98b~mv2.jpg/v1/fill/w_640,h_426,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/91e241_e4c09e48b2ab442bae84592abe07d98b~mv2.jpg'
);INSERT INTO Recipes (Name, Description, Ingredients, Category, SubmissionDate, ImageUrl)
VALUES (
    'Mutton Rogan Josh',
    'Mutton Rogan Josh is a traditional Kashmiri curry known for its deep red color and rich flavor. It is made with succulent mutton pieces cooked in a spicy and aromatic gravy. Follow these steps to prepare this classic dish:\n\n
    1. Heat 3 tablespoons of oil or ghee in a heavy-bottomed pan.
    2. Add whole spices like cloves, cinnamon, and cardamom pods. Sauté for a minute.
    3. Add finely chopped onions and cook until they turn golden brown.
    4. Add ginger-garlic paste and cook until the raw smell disappears.
    5. Add mutton pieces and cook until they are browned on all sides.
    6. Sprinkle spices like turmeric, red chili powder, and Kashmiri chili powder. Mix well.
    7. Add yogurt and cook until the oil separates from the masala.
    8. Pour in a cup of water, cover, and simmer until the mutton is tender.
    9. Garnish with fresh coriander leaves and serve hot!',
    '- 500 grams Mutton\n- 3 tablespoons Oil or Ghee\n- 4-5 Cloves\n- 1-inch Cinnamon Stick\n- 4-5 Green Cardamom Pods\n- 2 Onions, finely chopped\n- 1 tablespoon Ginger-Garlic Paste\n- 1/2 teaspoon Turmeric Powder\n- 2 teaspoons Red Chili Powder\n- 2 teaspoons Kashmiri Chili Powder\n- 1/2 cup Yogurt\n- Fresh Coriander Leaves for garnish\n- Salt to taste',
    'Mutton',
    '2023-10-10',
    'https://d36v5spmfzyapc.cloudfront.net/wp-content/uploads/2018/08/kashmiri-rogan-josh-1-848x424.jpg'
);

INSERT INTO Recipes (Name, Description, Ingredients, Category, SubmissionDate, ImageUrl)
VALUES (
    'Mutton Korma',
    'Mutton Korma is a classic Indian curry made with tender mutton pieces cooked in a creamy and aromatic gravy. Its a perfect dish for special occasions. Follow these steps to prepare this delightful dish:\n\n
    1. Heat 3 tablespoons of ghee or oil in a heavy-bottomed pan.
    2. Add whole spices like bay leaves, cinnamon, cardamom, cloves, and peppercorns. Sauté for a minute until fragrant.
    3. Add finely sliced onions and cook until they turn golden brown.
    4. Add ginger-garlic paste and cook until the raw smell disappears.
    5. Add mutton pieces and cook until they are browned on all sides.
    6. Sprinkle spices like turmeric, red chili powder, and coriander powder. Mix well.
    7. Add yogurt and cook until the oil separates from the masala.
    8. Pour in a cup of water, cover, and simmer until the mutton is tender.
    9. Add cream or ground cashews for a creamy texture.
    10. Garnish with chopped coriander leaves and serve hot!',
    '- 500 grams Mutton\n- 3 tablespoons Ghee or Oil\n- 2 Bay Leaves\n- 1-inch Cinnamon Stick\n- 4-5 Green Cardamom Pods\n- 4-5 Cloves\n- 4-5 Black Peppercorns\n- 2 Onions, thinly sliced\n- 1 tablespoon Ginger-Garlic Paste\n- 1/2 teaspoon Turmeric Powder\n- 2 teaspoons Red Chili Powder\n- 2 teaspoons Coriander Powder\n- 1/2 cup Yogurt\n- Cream or Ground Cashews (for creaminess)\n- Fresh Coriander Leaves for garnish\n- Salt to taste',
    'Mutton',
    '2023-10-11',
    'https://i1.wp.com/www.bharatzkitchen.com/wp-content/uploads/2019/08/mutton-5.jpg?fit=800%2C450&ssl=1'
);
INSERT INTO Recipes (Name, Description, Ingredients, Category, SubmissionDate, ImageUrl)
VALUES (
